SELECT chn.name,t.title   FROM   cast_info AS ci, char_name AS chn, company_name AS cn, company_type AS ct, movie_companies AS mc, role_type AS rt, title AS t  WHERE chn.id = ci.person_role_id AND ci.movie_id = mc.movie_id AND cn.id = mc.company_id AND ct.id = mc.company_type_id AND rt.id = ci.role_id AND t.id = ci.movie_id AND t.id = mc.movie_id AND ((ci.note)::text ~~ '%(voice)%'::text) AND ((ci.note)::text ~~ '%(uncredited)%'::text) AND ((cn.country_code)::text = '[ru]'::text) AND ((rt.role)::text = 'actor'::text) AND (t.production_year > 2005);