SELECT cn.name,mc.note,t.title   FROM   company_name AS cn, company_type AS ct, keyword AS k, link_type AS lt, movie_companies AS mc, movie_keyword AS mk, movie_link AS ml, title AS t  WHERE lt.id = ml.link_type_id AND mc.company_id = cn.id AND mc.company_type_id = ct.id AND mk.keyword_id = k.id AND mk.movie_id = mc.movie_id AND ml.movie_id = mc.movie_id AND ml.movie_id = mk.movie_id AND ml.movie_id = t.id AND t.id = mc.movie_id AND t.id = mk.movie_id AND ((cn.country_code)::text <> '[pl]'::text) AND (((cn.name)::text ~~ '20th Century Fox%'::text) OR ((cn.name)::text ~~ 'Twentieth Century Fox%'::text)) AND ((ct.kind)::text <> 'production companies'::text) AND (ct.kind IS NOT NULL) AND ((k.keyword)::text = ANY ('{sequel,revenge,based-on-novel}'::text[])) AND (mc.note IS NOT NULL) AND (t.production_year > 1950);