SELECT mi.info,t.title   FROM   company_name AS cn, company_type AS ct, info_type AS it1, info_type AS it2, movie_companies AS mc, movie_info AS mi, movie_info_idx AS mi_idx, title AS t  WHERE cn.id = mc.company_id AND ct.id = mc.company_type_id AND mc.movie_id = mi.movie_id AND mc.movie_id = mi_idx.movie_id AND mi.info_type_id = it1.id AND mi.movie_id = mi_idx.movie_id AND mi_idx.info_type_id = it2.id AND t.id = mc.movie_id AND t.id = mi.movie_id AND t.id = mi_idx.movie_id AND ((cn.country_code)::text = '[us]'::text) AND ((it1.info)::text = 'budget'::text) AND ((it2.info)::text = 'bottom 10 rank'::text) AND (ct.kind IS NOT NULL) AND (((ct.kind)::text = 'production companies'::text) OR ((ct.kind)::text = 'distributors'::text)) AND (t.production_year > 2000) AND (((t.title)::text ~~ 'Birdemic%'::text) OR ((t.title)::text ~~ '%Movie%'::text));