SELECT cn.name,mi_idx.info,t.title   FROM   company_name AS cn, company_type AS ct, info_type AS it1, info_type AS it2, movie_companies AS mc, movie_info AS mi, movie_info_idx AS mi_idx, title AS t  WHERE cn.id = mc.company_id AND ct.id = mc.company_type_id AND mc.movie_id = mi.movie_id AND mc.movie_id = mi_idx.movie_id AND mi.info_type_id = it1.id AND mi.movie_id = mi_idx.movie_id AND mi_idx.info_type_id = it2.id AND t.id = mc.movie_id AND t.id = mi.movie_id AND t.id = mi_idx.movie_id AND ((cn.country_code)::text = '[us]'::text) AND ((ct.kind)::text = 'production companies'::text) AND ((it1.info)::text = 'genres'::text) AND ((it2.info)::text = 'rating'::text) AND ((mi.info)::text = ANY ('{Drama,Horror,Western,Family}'::text[])) AND ((mi_idx.info)::text > '7.0'::text) AND (t.production_year >= 2000) AND (t.production_year <= 2010);