SELECT mi.info,t.title   FROM   aka_title AS aka_t, company_name AS cn, company_type AS ct, info_type AS it1, keyword AS k, movie_companies AS mc, movie_info AS mi, movie_keyword AS mk, title AS t  WHERE cn.id = mc.company_id AND ct.id = mc.company_type_id AND it1.id = mi.info_type_id AND k.id = mk.keyword_id AND mc.movie_id = aka_t.movie_id AND mi.movie_id = aka_t.movie_id AND mi.movie_id = mc.movie_id AND mk.movie_id = aka_t.movie_id AND mk.movie_id = mc.movie_id AND mk.movie_id = mi.movie_id AND t.id = aka_t.movie_id AND t.id = mc.movie_id AND t.id = mi.movie_id AND t.id = mk.movie_id AND ((cn.country_code)::text = '[us]'::text) AND ((it1.info)::text = 'release dates'::text) AND ((mc.note)::text ~~ '%(200%)%'::text) AND ((mc.note)::text ~~ '%(worldwide)%'::text) AND ((mi.note)::text ~~ '%internet%'::text) AND ((mi.info)::text ~~ 'USA:% 200%'::text) AND (t.production_year > 2000);