SELECT n.name   FROM   cast_info AS ci, company_name AS cn, keyword AS k, movie_companies AS mc, movie_keyword AS mk, name AS n, title AS t  WHERE ci.movie_id = mc.movie_id AND ci.movie_id = mk.movie_id AND ci.movie_id = t.id AND mc.company_id = cn.id AND mc.movie_id = mk.movie_id AND mk.keyword_id = k.id AND n.id = ci.person_id AND t.id = mc.movie_id AND t.id = mk.movie_id AND ((cn.country_code)::text = '[us]'::text) AND ((k.keyword)::text = 'character-name-in-title'::text);