SELECT mc.note,t.production_year,t.title   FROM   company_type AS ct, info_type AS it, movie_companies AS mc, movie_info_idx AS mi_idx, title AS t  WHERE ct.id = mc.company_type_id AND it.id = mi_idx.info_type_id AND mc.movie_id = mi_idx.movie_id AND t.id = mc.movie_id AND t.id = mi_idx.movie_id AND ((ct.kind)::text = 'production companies'::text) AND ((it.info)::text = 'top 250 rank'::text) AND ((mc.note)::text !~~ '%(as Metro-Goldwyn-Mayer Pictures)%'::text) AND (((mc.note)::text ~~ '%(co-production)%'::text) OR ((mc.note)::text ~~ '%(presents)%'::text));