SELECT t.title   FROM   cast_info AS ci, char_name AS chn, comp_cast_type AS cct1, comp_cast_type AS cct2, complete_cast AS cc, keyword AS k, kind_type AS kt, movie_keyword AS mk, name AS n, title AS t  WHERE cct1.id = cc.subject_id AND cct2.id = cc.status_id AND chn.id = ci.person_role_id AND ci.movie_id = cc.movie_id AND k.id = mk.keyword_id AND kt.id = t.kind_id AND mk.movie_id = cc.movie_id AND mk.movie_id = ci.movie_id AND n.id = ci.person_id AND t.id = cc.movie_id AND t.id = ci.movie_id AND t.id = mk.movie_id AND ((cct1.kind)::text = 'cast'::text) AND ((cct2.kind)::text ~~ '%complete%'::text) AND ((chn.name)::text !~~ '%Sherlock%'::text) AND (((chn.name)::text ~~ '%Tony%Stark%'::text) OR ((chn.name)::text ~~ '%Iron%Man%'::text)) AND ((k.keyword)::text = ANY ('{superhero,sequel,second-part,marvel-comics,based-on-comic,tv-special,fight,violence}'::text[])) AND ((kt.kind)::text = 'movie'::text) AND (t.production_year > 1950);