SELECT cn.name,lt.link,t.title   FROM   company_name AS cn, company_type AS ct, keyword AS k, link_type AS lt, movie_companies AS mc, movie_info AS mi, movie_keyword AS mk, movie_link AS ml, title AS t  WHERE lt.id = ml.link_type_id AND mc.company_id = cn.id AND mc.company_type_id = ct.id AND mc.movie_id = mi.movie_id AND mi.movie_id = t.id AND mk.keyword_id = k.id AND mk.movie_id = mc.movie_id AND mk.movie_id = mi.movie_id AND ml.movie_id = mc.movie_id AND ml.movie_id = mi.movie_id AND ml.movie_id = mk.movie_id AND ml.movie_id = t.id AND t.id = mc.movie_id AND t.id = mk.movie_id AND ((cn.country_code)::text <> '[pl]'::text) AND (((cn.name)::text ~~ '%Film%'::text) OR ((cn.name)::text ~~ '%Warner%'::text)) AND ((ct.kind)::text = 'production companies'::text) AND ((k.keyword)::text = 'sequel'::text) AND ((lt.link)::text ~~ '%follow%'::text) AND ((mi.info)::text = ANY ('{Germany,German}'::text[])) AND (mc.note IS NULL) AND (t.production_year >= 2000) AND (t.production_year <= 2010);