SELECT cn.name,mi_idx.info,t.title   FROM   company_name AS cn, company_type AS ct, info_type AS it1, info_type AS it2, keyword AS k, kind_type AS kt, movie_companies AS mc, movie_info AS mi, movie_info_idx AS mi_idx, movie_keyword AS mk, title AS t  WHERE cn.id = mc.company_id AND ct.id = mc.company_type_id AND it1.id = mi.info_type_id AND it2.id = mi_idx.info_type_id AND k.id = mk.keyword_id AND kt.id = t.kind_id AND mc.movie_id = mi_idx.movie_id AND mi.movie_id = mc.movie_id AND mi.movie_id = mi_idx.movie_id AND mk.movie_id = mc.movie_id AND mk.movie_id = mi.movie_id AND mk.movie_id = mi_idx.movie_id AND t.id = mc.movie_id AND t.id = mi.movie_id AND t.id = mi_idx.movie_id AND t.id = mk.movie_id AND ((cn.country_code)::text <> '[us]'::text) AND ((it1.info)::text = 'countries'::text) AND ((it2.info)::text = 'rating'::text) AND ((k.keyword)::text = ANY ('{murder,murder-in-title,blood,violence}'::text[])) AND ((kt.kind)::text = ANY ('{movie,episode}'::text[])) AND ((mc.note)::text !~~ '%(USA)%'::text) AND ((mc.note)::text ~~ '%(200%)%'::text) AND ((mi.info)::text = ANY ('{Sweden,Norway,Germany,Denmark,Swedish,Danish,Norwegian,German,USA,American}'::text[])) AND ((mi_idx.info)::text < '8.5'::text) AND (t.production_year > 2005);