SELECT mi.info,mi_idx.info,n.name,t.title   FROM   cast_info AS ci, info_type AS it1, info_type AS it2, keyword AS k, movie_info AS mi, movie_info_idx AS mi_idx, movie_keyword AS mk, name AS n, title AS t  WHERE ci.movie_id = mi.movie_id AND ci.movie_id = mi_idx.movie_id AND ci.movie_id = mk.movie_id AND it1.id = mi.info_type_id AND it2.id = mi_idx.info_type_id AND k.id = mk.keyword_id AND mi.movie_id = mi_idx.movie_id AND mi.movie_id = mk.movie_id AND mi_idx.movie_id = mk.movie_id AND n.id = ci.person_id AND t.id = ci.movie_id AND t.id = mi.movie_id AND t.id = mi_idx.movie_id AND t.id = mk.movie_id AND ((ci.note)::text = ANY ('{(writer),(head writer),(written by),(story),(story editor)}'::text[])) AND ((it1.info)::text = 'genres'::text) AND ((it2.info)::text = 'votes'::text) AND ((k.keyword)::text = ANY ('{murder,blood,gore,death,female-nudity}'::text[])) AND ((mi.info)::text = 'Horror'::text) AND ((n.gender)::text = 'm'::text) AND (t.production_year > 2010) AND ((t.title)::text ~~ 'Vampire%'::text);