SELECT t.title   FROM   company_name AS cn, keyword AS k, movie_companies AS mc, movie_keyword AS mk, title AS t  WHERE cn.id = mc.company_id AND mc.movie_id = mk.movie_id AND mc.movie_id = t.id AND mk.keyword_id = k.id AND t.id = mk.movie_id AND ((cn.country_code)::text = '[us]'::text) AND ((k.keyword)::text = 'character-name-in-title'::text);