SELECT mi.info,mi_idx.info,n.name,t.title   FROM   cast_info AS ci, comp_cast_type AS cct1, comp_cast_type AS cct2, complete_cast AS cc, info_type AS it1, info_type AS it2, keyword AS k, movie_info AS mi, movie_info_idx AS mi_idx, movie_keyword AS mk, name AS n, title AS t  WHERE cct1.id = cc.subject_id AND cct2.id = cc.status_id AND ci.movie_id = cc.movie_id AND ci.movie_id = mi.movie_id AND ci.movie_id = mi_idx.movie_id AND ci.movie_id = mk.movie_id AND it1.id = mi.info_type_id AND it2.id = mi_idx.info_type_id AND k.id = mk.keyword_id AND mi.movie_id = cc.movie_id AND mi.movie_id = mi_idx.movie_id AND mi.movie_id = mk.movie_id AND mi_idx.movie_id = cc.movie_id AND mi_idx.movie_id = mk.movie_id AND mk.movie_id = cc.movie_id AND n.id = ci.person_id AND t.id = cc.movie_id AND t.id = ci.movie_id AND t.id = mi.movie_id AND t.id = mi_idx.movie_id AND t.id = mk.movie_id AND ((cct1.kind)::text = 'cast'::text) AND ((cct2.kind)::text = 'complete+verified'::text) AND ((ci.note)::text = ANY ('{(writer),(head writer),(written by),(story),(story editor)}'::text[])) AND ((it1.info)::text = 'genres'::text) AND ((it2.info)::text = 'votes'::text) AND ((k.keyword)::text = ANY ('{murder,violence,blood,gore,death,female-nudity,hospital}'::text[])) AND ((mi.info)::text = ANY ('{Horror,Action,Sci-Fi,Thriller,Crime,War}'::text[])) AND ((n.gender)::text = 'm'::text);