SELECT t.title   FROM   keyword AS k, movie_info AS mi, movie_keyword AS mk, title AS t  WHERE k.id = mk.keyword_id AND mk.movie_id = mi.movie_id AND t.id = mi.movie_id AND t.id = mk.movie_id AND ((k.keyword)::text ~~ '%sequel%'::text) AND ((mi.info)::text = ANY ('{Sweden,Norway,Germany,Denmark,Swedish,Denish,Norwegian,German,USA,American}'::text[])) AND (t.production_year > 1990);