SELECT mi_idx.info,t.title   FROM   info_type AS it, keyword AS k, movie_info_idx AS mi_idx, movie_keyword AS mk, title AS t  WHERE it.id = mi_idx.info_type_id AND k.id = mk.keyword_id AND mk.movie_id = mi_idx.movie_id AND t.id = mi_idx.movie_id AND t.id = mk.movie_id AND ((it.info)::text = 'rating'::text) AND ((k.keyword)::text ~~ '%sequel%'::text) AND ((mi_idx.info)::text > '5.0'::text) AND (t.production_year > 2005);