SELECT t.title   FROM   company_type AS ct, info_type AS it, movie_companies AS mc, movie_info AS mi, title AS t  WHERE ct.id = mc.company_type_id AND it.id = mi.info_type_id AND mc.movie_id = mi.movie_id AND t.id = mc.movie_id AND t.id = mi.movie_id AND ((ct.kind)::text = 'production companies'::text) AND ((mc.note)::text ~~ '%(VHS)%'::text) AND ((mc.note)::text ~~ '%(USA)%'::text) AND ((mc.note)::text ~~ '%(1994)%'::text) AND ((mi.info)::text = ANY ('{USA,America}'::text[])) AND (t.production_year > 2010);