SELECT k.keyword,n.name,t.title   FROM   cast_info AS ci, keyword AS k, movie_keyword AS mk, name AS n, title AS t  WHERE ci.movie_id = mk.movie_id AND k.id = mk.keyword_id AND n.id = ci.person_id AND t.id = ci.movie_id AND t.id = mk.movie_id AND ((k.keyword)::text = 'marvel-cinematic-universe'::text) AND ((n.name)::text ~~ '%Downey%Robert%'::text) AND (t.production_year > 2014);