SELECT n.name,t.title   FROM   aka_name AS an, cast_info AS ci, info_type AS it, link_type AS lt, movie_link AS ml, name AS n, person_info AS pi, title AS t  WHERE an.person_id = ci.person_id AND ci.movie_id = ml.linked_movie_id AND ci.person_id = n.id AND it.id = pi.info_type_id AND lt.id = ml.link_type_id AND ml.linked_movie_id = t.id AND n.id = an.person_id AND n.id = pi.person_id AND pi.person_id = an.person_id AND pi.person_id = ci.person_id AND t.id = ci.movie_id AND ((an.name)::text ~~ '%a%'::text) AND ((it.info)::text = 'mini biography'::text) AND ((lt.link)::text = 'features'::text) AND ((n.name_pcode_cf)::text ~~ 'D%'::text) AND ((n.gender)::text = 'm'::text) AND ((pi.note)::text = 'Volker Boehm'::text) AND (t.production_year >= 1980) AND (t.production_year <= 1984);