SELECT an1.name,t.title   FROM   aka_name AS an1, cast_info AS ci, company_name AS cn, movie_companies AS mc, name AS n1, role_type AS rt, title AS t  WHERE an1.person_id = ci.person_id AND an1.person_id = n1.id AND ci.movie_id = mc.movie_id AND ci.movie_id = t.id AND ci.role_id = rt.id AND mc.company_id = cn.id AND n1.id = ci.person_id AND t.id = mc.movie_id AND ((ci.note)::text = '(voice: English version)'::text) AND ((cn.country_code)::text = '[jp]'::text) AND ((mc.note)::text ~~ '%(Japan)%'::text) AND ((mc.note)::text !~~ '%(USA)%'::text) AND ((n1.name)::text ~~ '%Yo%'::text) AND ((n1.name)::text !~~ '%Yu%'::text) AND ((rt.role)::text = 'actress'::text);