SELECT an.name,t.title   FROM   aka_name AS an, cast_info AS ci, company_name AS cn, movie_companies AS mc, name AS n, role_type AS rt, title AS t  WHERE an.person_id = ci.person_id AND an.person_id = n.id AND ci.movie_id = mc.movie_id AND ci.movie_id = t.id AND ci.role_id = rt.id AND mc.company_id = cn.id AND n.id = ci.person_id AND t.id = mc.movie_id AND ((ci.note)::text = '(voice: English version)'::text) AND ((cn.country_code)::text = '[jp]'::text) AND ((mc.note)::text ~~ '%(Japan)%'::text) AND ((mc.note)::text !~~ '%(USA)%'::text) AND (((mc.note)::text ~~ '%(2006)%'::text) OR ((mc.note)::text ~~ '%(2007)%'::text)) AND ((n.name)::text ~~ '%Yo%'::text) AND ((n.name)::text !~~ '%Yu%'::text) AND ((rt.role)::text = 'actress'::text) AND (t.production_year >= 2006) AND (t.production_year <= 2007) AND (((t.title)::text ~~ 'One Piece%'::text) OR ((t.title)::text ~~ 'Dragon Ball Z%'::text));