SELECT an.name,chn.name,t.title   FROM   aka_name AS an, cast_info AS ci, char_name AS chn, company_name AS cn, movie_companies AS mc, name AS n, role_type AS rt, title AS t  WHERE an.person_id = ci.person_id AND an.person_id = n.id AND chn.id = ci.person_role_id AND ci.movie_id = mc.movie_id AND ci.movie_id = t.id AND ci.role_id = rt.id AND mc.company_id = cn.id AND n.id = ci.person_id AND t.id = mc.movie_id AND ((ci.note)::text = ANY ('{(voice),(voice: Japanese version),(voice) (uncredited),(voice: English version)}'::text[])) AND ((cn.country_code)::text = '[us]'::text) AND ((n.name)::text ~~ '%Ang%'::text) AND ((n.gender)::text = 'f'::text) AND ((rt.role)::text = 'actress'::text) AND (mc.note IS NOT NULL) AND (((mc.note)::text ~~ '%(USA)%'::text) OR ((mc.note)::text ~~ '%(worldwide)%'::text)) AND (t.production_year >= 2005) AND (t.production_year <= 2015);