SELECT an.name,chn.name,n.name,t.title   FROM   aka_name AS an, cast_info AS ci, char_name AS chn, company_name AS cn, movie_companies AS mc, name AS n, role_type AS rt, title AS t  WHERE an.person_id = ci.person_id AND an.person_id = n.id AND chn.id = ci.person_role_id AND ci.movie_id = mc.movie_id AND ci.movie_id = t.id AND ci.role_id = rt.id AND mc.company_id = cn.id AND n.id = ci.person_id AND t.id = mc.movie_id AND ((ci.note)::text = '(voice)'::text) AND ((cn.country_code)::text = '[us]'::text) AND ((mc.note)::text ~~ '%(200%)%'::text) AND (((mc.note)::text ~~ '%(USA)%'::text) OR ((mc.note)::text ~~ '%(worldwide)%'::text)) AND ((n.name)::text ~~ '%Angel%'::text) AND ((n.gender)::text = 'f'::text) AND ((rt.role)::text = 'actress'::text) AND (t.production_year >= 2007) AND (t.production_year <= 2010);